import{_ as e,c as a,o as t,a as o}from"./app.f452003e.js";const p=JSON.parse('{"title":"Welcome to Interlock!","description":"","frontmatter":{},"headers":[{"level":2,"title":"What is a handbook?","slug":"what-is-a-handbook"},{"level":2,"title":"Inspiration","slug":"inspiration"},{"level":2,"title":"Handbook style guide","slug":"handbook-style-guide"},{"level":3,"title":"Headings","slug":"headings"},{"level":2,"title":"Contributing to the handbook","slug":"contributing-to-the-handbook"}],"relativePath":"handbook/index.md"}'),n={name:"handbook/index.md"},i=o('<h1 id="welcome-to-interlock" tabindex="-1">Welcome to Interlock! <a class="header-anchor" href="#welcome-to-interlock" aria-hidden="true">#</a></h1><h2 id="what-is-a-handbook" tabindex="-1">What is a handbook? <a class="header-anchor" href="#what-is-a-handbook" aria-hidden="true">#</a></h2><p>The Interlock handbook is the central guide for how we run things. As part of our value of being transparent, the handbook is open to the world. We welcome feedback, and contributions, so please click to edit and suggest improvements throughout these pages.</p><h2 id="inspiration" tabindex="-1">Inspiration <a class="header-anchor" href="#inspiration" aria-hidden="true">#</a></h2><p>The Interlock handbook is inspired by the <a href="https://about.gitlab.com/handbook/" target="_blank" rel="noopener noreferrer">GitLab team handbook</a> and resources from <a href="https://www.ycombinator.com/" target="_blank" rel="noopener noreferrer">YCombinator</a>. It shares the same advantages and will probably undergo a similar evolution.</p><p>While GitLab&#39;s handbook inspires this handbook, it is nowhere near as complete (yet!) We will continue adding and updating this handbook and gradually integrating information from Interlock&#39;s <a href="https://github.com/interlock-network/knowledgebase-public" target="_blank" rel="noopener noreferrer">knowledge base</a> as time and growth permits.</p><h2 id="handbook-style-guide" tabindex="-1">Handbook style guide <a class="header-anchor" href="#handbook-style-guide" aria-hidden="true">#</a></h2><h3 id="headings" tabindex="-1">Headings <a class="header-anchor" href="#headings" aria-hidden="true">#</a></h3><p>Headings help readers scan content to find what they need easily. Organize page content using clear headings specific to the topic they describe.</p><p>Keep headings brief and organize them in a logical order:</p><ul><li>H1: Page title</li><li>H2: Main headings</li><li>H3: Subheadings</li><li>H4: Sub-subheadings (headings nested under subheadings)</li></ul><p>Try to stay within three or four heading levels. Complicated documents may use more, but pages with a simpler structure are easier to read.</p><h2 id="contributing-to-the-handbook" tabindex="-1">Contributing to the handbook <a class="header-anchor" href="#contributing-to-the-handbook" aria-hidden="true">#</a></h2><p>To contribute to a handbook page:</p><ol><li>Click &quot;Edit this page.&quot;</li><li>Make your changes in the browser(The language is <a href="https://github.github.com/gfm/" target="_blank" rel="noopener noreferrer">Markdown</a>).</li><li>Click &quot;Propose changes.&quot;</li><li>Click &quot;Create pull request.&quot;</li></ol><p>All done!</p>',16),r=[i];function h(s,d,l,c,u,g){return t(),a("div",null,r)}var k=e(n,[["render",h]]);export{p as __pageData,k as default};
